const os  = require('os');

module.exports = {
    OS_Arch: function(){
        const Arch = os.arch(); 
        return Arch;    
    },
    OS_Cpus: function(){
        return os.cpus();
    },
    Free_Mem: function(){
        Mem_Size={
            "Mem_In_Bytes" : parseInt(os.freemem()),
            "Mem_In_MBs" :   parseFloat(parseFloat(os.freemem()/(1024*1024)).toFixed(2)),  
           "Mem_In_GBs" :   parseFloat(parseFloat(os.freemem()/(1024*1024*1024)).toFixed(2)) 
        }
        return Mem_Size;
    },
    Host_Name: function(){
        return os.hostname();
    },
    PlatForm: function(){
        return os.platform();
    },
    Total_Memory:function(){
        return parseFloat(parseFloat(os.totalmem()/(1024*1024*1024)).toFixed(2));
    },
    OS_Type: function(){
        return os.type();
    }


}

